SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS equipamento_cirurgia_atual ON equipamento_cirurgia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_equipamento_cirurgia_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w		smallint;
ie_tipo_w		varchar(10);
ie_libera_partic_w	varchar(10);
nr_atendimento_w   bigint;
cd_pessoa_fisica_w varchar(10);
ie_libera_equipamento_w	varchar(5);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	coalesce(max(ie_libera_equipamento),'N')
into STRICT		ie_libera_equipamento_w
from		parametro_medico
where		cd_estabelecimento = obter_estabelecimento_ativo;

if (ie_libera_equipamento_w = 'S') then
	if (coalesce(NEW.nr_seq_pepo,0) <> 0) then
		select	max(c.nr_atendimento),
			max(c.cd_pessoa_fisica)
		into STRICT	nr_atendimento_w,
			cd_pessoa_fisica_w
		from	pepo_cirurgia c
		where	c.nr_sequencia = NEW.nr_seq_pepo;
	else
		select	max(c.nr_atendimento),
				max(c.cd_pessoa_fisica)
		into STRICT	nr_atendimento_w,
				cd_pessoa_fisica_w
		from	cirurgia c
		where	c.nr_cirurgia = NEW.nr_cirurgia;
	end if;

	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'EFAN';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XEFAN';
	end if;

	if (ie_tipo_w	is not null) then
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, cd_pessoa_fisica_w, nr_atendimento_w, NEW.nm_usuario);
	end if;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_equipamento_cirurgia_atual() FROM PUBLIC;

CREATE TRIGGER equipamento_cirurgia_atual
	AFTER INSERT OR UPDATE ON equipamento_cirurgia FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_equipamento_cirurgia_atual();

