CREATE OR REPLACE FUNCTION trigger_fct_escala_acef_atual() RETURNS trigger AS $BODY$
DECLARE
    sql_w       varchar(200);
    qt_score_w  real;
    qt_reg_w    smallint;
    ds_erro_w   varchar(2000);
    ds_parametro_w  varchar(2000);
BEGIN
  BEGIN
    IF ( wheb_usuario_pck.get_ie_executar_trigger = 'N' ) THEN
        GOTO final;
    END IF;
    IF ( NEW.nr_hora IS NULL ) OR ( NEW.dt_avaliacao <> OLD.dt_avaliacao ) THEN
        BEGIN
            NEW.nr_hora := (to_char(round(NEW.dt_avaliacao, 'hh24'), 'hh24'))::numeric;

        END;

    END IF;
--INICIO MD
    BEGIN
        sql_w := 'CALL OBTER_SCORE_ESCALA_ACEF_MD(:1, :2, :3) INTO :qt_score';
        EXECUTE sql_w
            USING IN NEW.qt_idade, IN NEW.qt_fracao_ejecao, IN NEW.qt_creatinina_serica, OUT qt_score_w;

    EXCEPTION
        WHEN OTHERS THEN
            qt_score_w := NULL;
            ds_erro_w := sqlerrm;ds_parametro_w := ':new.nr_sequencia: ' || NEW.nr_sequencia || ' - :new.nr_atendimento: ' || NEW.nr_atendimento || ' - :new.ie_situacao: ' || NEW.ie_situacao;
            ds_parametro_w := ds_parametro_w || ':new.qt_idade: ' || NEW.qt_idade || ' - :new.qt_fracao_ejecao: ' || NEW.qt_fracao_ejecao || ' - :new.qt_creatinina_serica: ' || NEW.qt_creatinina_serica || ' - qt_score_w: ' || qt_score_w;
            CALL gravar_log_medical_device('escala_acef_atual', 'OBTER_SCORE_ESCALA_ACEF_MD', ds_parametro_w, ds_erro_w, wheb_usuario_pck.get_nm_usuario, 'N');
    END;

    NEW.qt_score := qt_score_w;
--FIM MD
    << final >>
    qt_reg_w := 0;
  END;
