DROP TRIGGER IF EXISTS escala_akin_tr ON escala_akin CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_akin_tr() RETURNS trigger AS $BODY$
declare
  EXEC_w     varchar(200);
  qt_ponto_w smallint := 0;
  qt_reg_w	 smallint;
BEGIN
  BEGIN
  if (NEW.nr_hora is null) or (NEW.DT_AVALIACAO <> OLD.DT_AVALIACAO) then
    BEGIN
    NEW.nr_hora	:= (to_char(round(NEW.DT_AVALIACAO,'hh24'),'hh24'))::numeric;
    end;
  end if;

  if (wheb_usuario_pck.get_ie_executar_trigger = 'N') then
    goto Final;
  end if;

  BEGIN
    EXEC_w := 'CALL CALCULA_AKIN_TR_MD(:1, :2) INTO :result';

    EXECUTE EXEC_w USING IN NEW.ie_nivel_serico,
                                   IN NEW.ie_debito_urinario,
                                   OUT qt_ponto_w;
  exception
    when others then
          qt_ponto_w := null;
  end;

  NEW.qt_pontuacao	:= qt_ponto_w;
<<Final>>
qt_reg_w := 0;
  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
