                                        in NEW.ie_calma,
                                        in NEW.ie_sinc_sem_bloqueio,
                                        in NEW.ie_sinc_sem_tx_respiratoria,
                                        in NEW.ie_sinc_sem_tosse,
                                        in NEW.ie_sinc_sem_musc_resp,
                                        in NEW.ie_relaxamento_face,
                                        out qt_score_w;
      exception
          when others then
            qt_score_w := null;
            ds_erro_w := sqlerrm;
            ds_parametro_w := ':new.nr_atendimento: ' || NEW.nr_atendimento || ' - :new.cd_profissional: ' || NEW.cd_profissional || ' - :new.ie_situacao: ' || NEW.ie_situacao;
            ds_parametro_w := ds_parametro_w || ' - :new.ie_consciencia: ' || NEW.ie_consciencia || ' - :new.ie_comp_ab_fec_olhos: ' || NEW.ie_comp_ab_fec_olhos || ' - :new.ie_comp_ab_boca: ' || NEW.ie_comp_ab_boca || ' - :new.ie_comp_olhar: ' || NEW.ie_comp_olhar || ' - :new.ie_comp_acenar_cabeca: ' || NEW.ie_comp_acenar_cabeca || ' - :new.ie_comp_ab_boca_fec_olhos: ' || NEW.ie_comp_ab_boca_fec_olhos;
            ds_parametro_w := ds_parametro_w || ' - :new.ie_calma: ' || NEW.ie_calma || ' - :new.ie_sinc_sem_bloqueio: ' || NEW.ie_sinc_sem_bloqueio || ' - :new.ie_sinc_sem_tx_respiratoria: ' || NEW.ie_sinc_sem_tx_respiratoria || ' - :new.ie_sinc_sem_tosse: ' || NEW.ie_sinc_sem_tosse || ' - :new.ie_sinc_sem_musc_resp: ' || NEW.ie_sinc_sem_musc_resp || ' - :new.ie_relaxamento_face: ' || NEW.ie_relaxamento_face || ' - qt_score_w: ' || qt_score_w;
            CALL gravar_log_medical_device('ESCALA_ATICE_ATUAL', 'ESCALA_ATICE', ds_parametro_w, ds_erro_w, wheb_usuario_pck.get_nm_usuario, 'N');
      end;

