CREATE OR REPLACE FUNCTION trigger_fct_escala_bishop_atual() RETURNS trigger AS $BODY$
declare
  sql_w          varchar(800);
  qt_pontuacao_w bigint;
BEGIN
  BEGIN

  if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

    BEGIN
  
      sql_w := 'call calcula_bishop_md(:1, :2, :3, :4, :5) into :qt_pontuacao_w';
      EXECUTE sql_w
        using in NEW.ie_dilatacao, in NEW.ie_efface, in NEW.ie_station, in NEW.ie_posicao, in NEW.ie_consistencia, out qt_pontuacao_w;

    exception
      when others then
        qt_pontuacao_w := null;
    end;
    NEW.qt_pontuacao := qt_pontuacao_w;

  end if;

  END;
RETURN NEW;
end;
