DROP TRIGGER IF EXISTS escala_candida_atual ON escala_candida CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_candida_atual() RETURNS trigger AS $BODY$
declare
    EXEC_w      varchar(200);
    qt_score_w  bigint := 0;
    ds_erro_w   varchar(4000);
    ds_parametro_w  varchar(4000);
BEGIN
  BEGIN
  if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
    BEGIN
      EXEC_w := 'CALL OBTER_SCORE_ESCALA_CANDIDA_MD(:1, :2, :3, :4) INTO :result';

      EXECUTE EXEC_w USING IN NEW.IE_COLON_CANDIDA,
                                     IN NEW.IE_CIRURGIA_UTI,
                                     IN NEW.IE_NPT,
                                     IN NEW.IE_SEPSE,
                                     OUT qt_score_w;
    exception
      when others then
        qt_score_w := null;
        ds_erro_w := sqlerrm;
        ds_parametro_w := ':new.nr_atendimento: ' || NEW.nr_atendimento || ' - :new.cd_profissional: ' || NEW.cd_profissional || ' - :new.ie_situacao: ' || NEW.ie_situacao;
        ds_parametro_w := ds_parametro_w || ' - :new.IE_COLON_CANDIDA: ' || NEW.IE_COLON_CANDIDA || ' - :new.IE_CIRURGIA_UTI: ' || NEW.IE_CIRURGIA_UTI || ' - :new.IE_NPT: ' || NEW.IE_NPT || ' - :new.IE_SEPSE: ' || NEW.IE_SEPSE || ' - qt_score_w: ' || qt_score_w;
        CALL gravar_log_medical_device('ESCALA_CANDIDA_ATUAL', 'OBTER_SCORE_ESCALA_CANDIDA_MD', ds_parametro_w, ds_erro_w, wheb_usuario_pck.get_nm_usuario, 'N');
    end;

    NEW.qt_score	:= qt_score_w;
  end if;
  END;
RETURN NEW;
end;
