SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS escala_capurro_atual ON escala_capurro CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_capurro_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w		bigint;
cd_estabelecimento_w	bigint;
ie_ig_capurro_w		varchar(5);

BEGIN

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	atendimento_paciente
where	nr_atendimento	= NEW.nr_atendimento;

select	count(*)
into STRICT	qt_reg_w
from	nascimento
where	nr_atendimento = NEW.nr_atendimento;

select	coalesce(max(ie_ig_capurro),'N')
into STRICT	ie_ig_capurro_w
from	parametro_medico
where	cd_estabelecimento	= cd_estabelecimento_w;

if (qt_reg_w > 0) and (ie_ig_capurro_w = 'S') then
	update	nascimento
	set	qt_sem_ig = NEW.QT_SEMANAS,
		qt_dia_ig = NEW.QT_DIAS
	where	nr_atendimento = NEW.nr_atendimento;
end if;


RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_escala_capurro_atual() FROM PUBLIC;

CREATE TRIGGER escala_capurro_atual
	BEFORE UPDATE ON escala_capurro FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_escala_capurro_atual();

