CREATE OR REPLACE FUNCTION trigger_fct_escala_chung_atual() RETURNS trigger AS $BODY$
declare
  EXEC_w                 varchar(200);
  qt_reg_w	             smallint;
  cd_setor_atendimento_w bigint;
BEGIN
  BEGIN
  if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
    goto Final;
  end if;

  BEGIN
    EXEC_w := 'CALL OBTER_SCORE_ESCALA_CHUNG_MD(:1,:2,:3,:4,:5) INTO :result';

    EXECUTE EXEC_w USING IN NEW.ie_sinais_vitais,
                                   IN NEW.ie_atividade,
                                   IN NEW.ie_nauseas,
                                   IN NEW.ie_dor,
                                   IN NEW.ie_sangramento,
                                   OUT qt_reg_w;
  exception
    when others then
          qt_reg_w := null;
  end;

  NEW.qt_chung	:= qt_reg_w;
  <<Final>>
  qt_reg_w	:= 0;
  END;
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
