        in NEW.ie_breathless, 
        in NEW.ie_limited_activity, 
        in NEW.ie_confident, 
        in NEW.ie_sleep, 
        in NEW.ie_energy,
        out qt_score_w;
    exception
      when others then
        qt_score_w := null;
      end;
      NEW.qt_score := qt_score_w;
  end if;
  END;
RETURN NEW;
end;
