                                    out ie_resultado_w;

    exception
      when others then
	    ie_resultado_w := null;
        ds_erro_w := sqlerrm;
        ds_parametro_w := ':new.nr_atendimento: ' || NEW.nr_atendimento || ' - :new.ie_situacao: ' || NEW.ie_situacao || ' - :new.cd_profissional: ' || NEW.cd_profissional;
        ds_parametro_w := ds_parametro_w || ' - :new.ie_queimacao: ' || NEW.ie_queimacao || ' - :new.ie_frio_dolorosa: ' || NEW.ie_frio_dolorosa || ' - :new.ie_choque_eletrico: ' || NEW.ie_choque_eletrico || ' - :new.ie_formigamento: ' || NEW.ie_formigamento || ' - :new.ie_coceira: ' || NEW.ie_coceira || ' - :new.ie_hipoestesia_toque: ' || NEW.ie_hipoestesia_toque;
