        CALL gravar_log_medical_device('ESCALA_DN4_ATUAL', 'OBTER_SCORE_ESCALA_DN4_MD', ds_parametro_w, ds_erro_w, wheb_usuario_pck.get_nm_usuario, 'N');
    end;
