SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS escala_grace_atual ON escala_grace CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_grace_atual() RETURNS trigger AS $BODY$
declare
  sql_w          varchar(200);
  qt_pontuacao_w smallint;
  ds_erro_w      varchar(4000);
  ds_parametro_w varchar(4000);
BEGIN
  BEGIN
  if (NEW.nr_hora is null) or (NEW.DT_AVALIACAO <> OLD.DT_AVALIACAO) then
    BEGIN
	  NEW.nr_hora := (to_char(round(NEW.DT_AVALIACAO,'hh24'),'hh24'))::numeric;
    end;
  end if;

  /** Medical Device **/

  if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
	  BEGIN
		sql_w := 'call obter_score_escala_grace_md(:1, :2, :3, :4, :5, :6, :7, :8) into :qt_pontuacao_w';
		EXECUTE sql_w using in NEW.qt_idade,
									  in NEW.ie_killip,
									  in NEW.qt_pa_sistolica,
									  in NEW.qt_freq_cardiaca,
									  in NEW.qt_creatinina,
									  in NEW.ie_marcador_cardiaco_ele,
									  in NEW.ie_parada_cardiaca_adm,
									  in NEW.ie_desvio_seg_st,			
									  out qt_pontuacao_w;
	  exception
		when others then
		  qt_pontuacao_w := null;
      ds_erro_w := sqlerrm;
      ds_parametro_w := ':new.nr_atendimento: ' || NEW.nr_atendimento || ' - :new.ie_situacao: ' || NEW.ie_situacao || ' - :new.cd_profissional: ' || NEW.cd_profissional;
      ds_parametro_w := ds_parametro_w || ' - :new.qt_idade: ' || NEW.qt_idade || ' - :new.ie_killip: ' || NEW.ie_killip || ' - :new.qt_pa_sistolica: ' || NEW.qt_pa_sistolica;
      ds_parametro_w := ds_parametro_w || ' - :new.qt_freq_cardiaca: ' || NEW.qt_freq_cardiaca || ' - :new.qt_creatinina: ' || NEW.qt_creatinina || ' - :new.ie_marcador_cardiaco_ele: ' || NEW.ie_marcador_cardiaco_ele || ' - :new.ie_parada_cardiaca_adm: ' || NEW.ie_parada_cardiaca_adm;
      ds_parametro_w := ds_parametro_w || ' - :new.ie_desvio_seg_st: ' || NEW.ie_desvio_seg_st || ' - qt_pontuacao_w: ' || qt_pontuacao_w;
      CALL gravar_log_medical_device('ESCALA_GRACE_ATUAL', 'OBTER_SCORE_ESCALA_GRACE_MD', ds_parametro_w, ds_erro_w, wheb_usuario_pck.get_nm_usuario, 'N');
	  end;
	  NEW.qt_pontuacao := qt_pontuacao_w;
  end if;
  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
