SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS escala_icdsc_atual ON escala_icdsc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_icdsc_atual() RETURNS trigger AS $BODY$
declare
  qt_reg_w	  smallint;
  sql_w       varchar(200);
  qt_ponto_w  smallint;
BEGIN
  BEGIN
  if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
      goto Final;
  end if;

  BEGIN
      sql_w := 'call OBTER_SCORE_ESCALA_ICDSC_MD(:1,:2,:3,:4,:5,:6,:7,:8) into :qt_ponto_w';

      EXECUTE sql_w using in NEW.ie_nivel_consciencia_alt,
                                    in NEW.ie_desatencao,
                                    in NEW.ie_desorientacao,
                                    in NEW.ie_alucinacao,
                                    in NEW.ie_agitacao,
                                    in NEW.ie_fala_emo_inap,
                                    in NEW.ie_disturbio_sono,
                                    in NEW.ie_flutuacao,
                                    out qt_ponto_w;
  exception
    when others then
          qt_ponto_w := null;
  end;

  NEW.qt_ponto := coalesce(qt_ponto_w,0);

  <<Final>>
  qt_reg_w	:= 0;
  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_escala_icdsc_atual() FROM PUBLIC;

CREATE TRIGGER escala_icdsc_atual
	BEFORE INSERT OR UPDATE ON escala_icdsc FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_escala_icdsc_atual();

