DROP TRIGGER IF EXISTS escala_ipss_atual ON escala_ipps CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_ipss_atual() RETURNS trigger AS $BODY$
declare
  qt_reg_w	     smallint;
  sql_w          varchar(200);
  qt_pontuacao_w smallint;
BEGIN
  BEGIN
  if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
  end if;

  /** Medical Device **/

  BEGIN 
  sql_w := 'call obter_score_escala_ipps_md(:1, :2, :3, :4, :5, :6, :7) into :qt_pontuacao_w';

  EXECUTE sql_w using  in NEW.ie_esvaziar_bexiga_total,
                                 in NEW.ie_urinar_2_horas,
                                 in NEW.ie_urinar_recomecou,
                                 in NEW.ie_dific_conter_urina,
                                 in NEW.ie_jato_fraco, 
                                 in NEW.ie_forca_urinar, 
                                 in NEW.ie_vezes_urinar_noite,
                                out qt_pontuacao_w;
   EXCEPTION
   WHEN OTHERS THEN
     NEW.qt_pontuacao	:= NULL;
   END;

  NEW.qt_pontuacao	:= 	qt_pontuacao_w;
<<Final>>
qt_reg_w := 0;
  END;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_escala_ipss_atual() FROM PUBLIC;

CREATE TRIGGER escala_ipss_atual
	BEFORE INSERT OR UPDATE ON escala_ipps FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_escala_ipss_atual();

