DROP TRIGGER IF EXISTS escala_katz_tr ON escala_katz CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_katz_tr() RETURNS trigger AS $BODY$
declare

sql_w          varchar(200);
qt_pontuacao_w smallint;
qt_reg_w	   smallint;
BEGIN
  BEGIN


if (NEW.nr_hora is null) or (NEW.DT_AVALIACAO <> OLD.DT_AVALIACAO) then
	BEGIN  
	NEW.nr_hora	:= (to_char(round(NEW.DT_AVALIACAO,'hh24'),'hh24'))::numeric;
	end;
end if;
if (wheb_usuario_pck.get_ie_executar_trigger = 'N') then
	goto Final;
end if;

  BEGIN
  sql_w := 'CALL OBTER_SCORE_ESCALA_KATZ_MD(:1, :2, :3, :4, :5, :6) INTO :qt_pontuacao_w';

  
  EXECUTE sql_w USING IN NEW.ie_banho, IN NEW.ie_vestir, 
                                IN NEW.ie_higiene_pessoal, IN NEW.ie_transferencia, 
                                IN NEW.ie_continencia, IN NEW.ie_alimentacao, OUT qt_pontuacao_w;
   EXCEPTION
   WHEN OTHERS THEN
      qt_pontuacao_w := null;
   END;
  NEW.qt_pontuacao	:= qt_pontuacao_w;

<<Final>>
qt_reg_w := 0;
  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
