DROP TRIGGER IF EXISTS escala_man_lf_atual ON escala_man_lf CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_man_lf_atual() RETURNS trigger AS $BODY$
declare
  peso_parte_w   double precision;
  qt_imc_w       real;
  qt_pontuacao_w real;
  sql_imc_w      varchar(200);
  sql_man_w      varchar(200);
  ds_erro_w      varchar(4000);
  ds_parametro_w varchar(4000);
BEGIN
  BEGIN

	if (wheb_usuario_pck.get_ie_executar_trigger	= 'S') then
	  /** Medical Device **/


	  BEGIN
	    peso_parte_w := null;
	    sql_imc_w := 'call obter_valor_imc_md(:1, :2, :3) into :qt_imc_w';
	    EXECUTE sql_imc_w using
        in NEW.qt_peso,
        in peso_parte_w,
        in NEW.qt_altura,
        out qt_imc_w;
	  exception
	    when others then
	      qt_imc_w := null;
        ds_erro_w := sqlerrm;
        ds_parametro_w := ':new.nr_atendimento: ' || NEW.nr_atendimento || ' - :new.ie_situacao: ' || NEW.ie_situacao || ' - :new.cd_profissional: ' || NEW.cd_profissional;
        ds_parametro_w := ds_parametro_w || ' - :new.qt_peso: ' || NEW.qt_peso || ' - peso_parte_w: ' || peso_parte_w || ' - :new.qt_altura: ' || NEW.qt_altura || ' - qt_imc_w: ' || qt_imc_w;
        CALL gravar_log_medical_device('ESCALA_MAN_LF_ATUAL', 'OBTER_VALOR_IMC_MD', ds_parametro_w, ds_erro_w, wheb_usuario_pck.get_nm_usuario, 'N');
	  end;
	  NEW.qt_imc := qt_imc_w;
	
	  BEGIN  
		sql_man_w := 'call obter_score_escala_man_md(:1, :2, :3, :4, :5, :6, :7) into :qt_pontuacao_w';
		EXECUTE sql_man_w using in qt_imc_w,
										  in NEW.qt_circun_panturrilha,
										  in NEW.ie_estresse,
										  in NEW.ie_diminuicao_alimentar,
										  in NEW.ie_perda_peso,
										  in NEW.ie_mobilidade,
										  in NEW.ie_neuropsicologicos,
                      out qt_pontuacao_w;
	  exception
		when others then
		  qt_pontuacao_w := null;
      ds_erro_w := sqlerrm;
      ds_parametro_w := ':new.nr_atendimento: ' || NEW.nr_atendimento || ' - :new.ie_situacao: ' || NEW.ie_situacao || ' - :new.cd_profissional: ' || NEW.cd_profissional;
      ds_parametro_w := ds_parametro_w || ' - qt_imc_w: ' || qt_imc_w || ' - :new.qt_circun_panturrilha: ' || NEW.qt_circun_panturrilha || ' - :new.ie_estresse: ' || NEW.ie_estresse;
      ds_parametro_w := ds_parametro_w || ' - :new.ie_diminuicao_alimentar: ' || NEW.ie_diminuicao_alimentar || ' - :new.ie_perda_peso: ' || NEW.ie_perda_peso || ' - :new.ie_mobilidade: ' || NEW.ie_mobilidade || ' - :new.ie_neuropsicologicos: ' || NEW.ie_neuropsicologicos;
      ds_parametro_w := ds_parametro_w || ' - qt_pontuacao_w: ' || qt_pontuacao_w;
      CALL gravar_log_medical_device('ESCALA_MAN_LF_ATUAL', 'OBTER_SCORE_ESCALA_MAN_MD', ds_parametro_w, ds_erro_w, wheb_usuario_pck.get_nm_usuario, 'N');
	  end;
	  NEW.qt_pontuacao	:= qt_pontuacao_w;
  end if;
  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_escala_man_lf_atual() FROM PUBLIC;

CREATE TRIGGER escala_man_lf_atual
	BEFORE INSERT OR UPDATE ON escala_man_lf FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_escala_man_lf_atual();

