DROP TRIGGER IF EXISTS escala_moca_atual ON escala_moca CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_moca_atual() RETURNS trigger AS $BODY$
declare

BEGIN

NEW.qt_score	 :=  NEW.QT_VISUO_ESPACIAL + NEW.QT_NOMEACAO + NEW.QT_ATENCAO +
		     NEW.QT_LINGUAGEM + NEW.QT_ABSTRACAO + NEW.QT_EVOCACAO + NEW.QT_ORIENTACAO;

if (NEW.qt_score < 30) and (NEW.IE_ESCOLARIDADE = 'S') then
	NEW.qt_score	:= 	NEW.qt_score + 1;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_escala_moca_atual() FROM PUBLIC;

CREATE TRIGGER escala_moca_atual
	BEFORE INSERT OR UPDATE ON escala_moca FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_escala_moca_atual();

