DROP TRIGGER IF EXISTS escala_morse_pend_atual ON escala_morse CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_morse_pend_atual() RETURNS trigger AS $BODY$
declare

pragma autonomous_transaction;

qt_reg_w		smallint;
ie_tipo_w		varchar(10);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;


if (NEW.dt_liberacao is null) then
	ie_tipo_w := 'ESC';
elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
	ie_tipo_w := 'XESC';
end if;

if (ie_tipo_w	is not null) then
	CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, substr(obter_pessoa_atendimento(NEW.nr_atendimento,'C'),1,255), NEW.nr_atendimento, coalesce(OBTER_USUARIO_PF(NEW.CD_PROFISSIONAL), NEW.nm_usuario),null, null, null, null, null, null,'89');
end if;

if (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
	CALL gerar_evolucao_Morse(NEW.nm_usuario,NEW.nr_sequencia);
end if;

commit;
	
<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_escala_morse_pend_atual() FROM PUBLIC;

CREATE TRIGGER escala_morse_pend_atual
	AFTER INSERT OR UPDATE ON escala_morse FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_escala_morse_pend_atual();

