                                        in OLD.ie_bocejo_frequente,
                                        in NEW.ie_manchas,
                                        in OLD.ie_manchas,
                                        in NEW.ie_entupim_nasal,
                                        in OLD.ie_entupim_nasal,
                                        in NEW.ie_espirros,
                                        in OLD.ie_espirros,
                                        in NEW.ie_dilatacao_narinas,
                                        in OLD.ie_dilatacao_narinas,
                                        in NEW.ie_succao_excessiva,
                                        in OLD.ie_succao_excessiva,
                                        in NEW.ie_ma_alimentacao,
                                        in OLD.ie_ma_alimentacao,
                                        in NEW.ie_regurgitacao,
                                        in OLD.ie_regurgitacao,
                                        in NEW.ie_choro,
                                        in NEW.ie_sono,
                                        in NEW.ie_reflexo_moro,
                                        in NEW.ie_tremores,
                                        in NEW.ie_febre,
                                        in NEW.ie_freq_respiratoria,
                                        in NEW.ie_fezes,
                                        out qt_score_w;
      exception
          when others then
            qt_score_w := null;
            ds_erro_w := sqlerrm;
            ds_parametro_w := ':new.ie_tonus_muscular: ' || NEW.ie_tonus_muscular || ' - :old.ie_tonus_muscular: ' || OLD.ie_tonus_muscular
            || ' - :new.ie_espasmos_mioclonicos: ' || NEW.ie_espasmos_mioclonicos || ' - :old.ie_espasmos_mioclonicos: ' || OLD.ie_espasmos_mioclonicos
            || ' - :new.ie_convulsoes: ' || NEW.ie_convulsoes || ' - :old.ie_convulsoes: ' || OLD.ie_convulsoes
            || ' - :new.ie_escoriacoes: ' || NEW.ie_escoriacoes || ' - :old.ie_escoriacoes: ' || OLD.ie_escoriacoes
            || ' - :new.ie_suor: ' || NEW.ie_suor || ' - :old.ie_suor: ' || OLD.ie_suor
            || ' - :new.ie_bocejo_frequente: ' || NEW.ie_bocejo_frequente || ' - :old.ie_bocejo_frequente: ' || OLD.ie_bocejo_frequente
            || ' - :new.ie_manchas: ' || NEW.ie_manchas || ' - :old.ie_manchas: ' || OLD.ie_manchas
            || ' - :new.ie_entupim_nasal: ' || NEW.ie_entupim_nasal || ' - :old.ie_entupim_nasal: ' || OLD.ie_entupim_nasal
            || ' - :new.ie_espirros: ' || NEW.ie_espirros || ' - :old.ie_espirros: ' || OLD.ie_espirros
            || ' - :new.ie_dilatacao_narinas: ' || NEW.ie_dilatacao_narinas || ' - :old.ie_dilatacao_narinas: ' || OLD.ie_dilatacao_narinas
            || ' - :new.ie_succao_excessiva: ' || NEW.ie_succao_excessiva || ' - :old.ie_succao_excessiva: ' || OLD.ie_succao_excessiva
            || ' - :new.ie_ma_alimentacao: ' || NEW.ie_ma_alimentacao || ' - :old.ie_ma_alimentacao: ' || OLD.ie_ma_alimentacao
            || ' - :new.ie_regurgitacao: ' || NEW.ie_regurgitacao || ' - :old.ie_regurgitacao: ' || OLD.ie_regurgitacao
            || ' - :new.ie_choro: ' || NEW.ie_choro || ' - :old.ie_choro: ' || OLD.ie_choro
            || ' - :new.ie_sono: ' || NEW.ie_sono || ' - :old.ie_sono: ' || OLD.ie_sono
            || ' - :new.ie_reflexo_moro: ' || NEW.ie_reflexo_moro || ' - :old.ie_reflexo_moro: ' || OLD.ie_reflexo_moro
            || ' - :new.ie_tremores: ' || NEW.ie_tremores || ' - :old.ie_tremores: ' || OLD.ie_tremores
            || ' - :new.ie_febre: ' || NEW.ie_febre || ' - :old.ie_febre: ' || OLD.ie_febre
            || ' - :new.ie_freq_respiratoria: ' || NEW.ie_freq_respiratoria || ' - :old.ie_freq_respiratoria: ' || OLD.ie_freq_respiratoria
            || ' - :new.ie_fezes: ' || NEW.ie_fezes || ' - :old.ie_fezes: ' || OLD.ie_fezes
            || ' - qt_score_w: ' || qt_score_w;
            CALL gravar_log_medical_device('ESCALA_NABSTINENCE_ATUAL', 'OBTER_SCORE_ESC_NABSTINENCE_MD', ds_parametro_w, ds_erro_w, wheb_usuario_pck.get_nm_usuario, 'N');
