SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS escala_possum_after_insert ON escala_possum CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_possum_after_insert() RETURNS trigger AS $BODY$
declare

qt_reg_w		smallint;
ie_tipo_w		varchar(10);
ie_libera_partic_w	varchar(10);
nr_atendimento_w   bigint;
cd_pessoa_fisica_w varchar(10);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	max(c.nr_atendimento),
			max(c.cd_pessoa_fisica)
into STRICT		nr_atendimento_w,
			cd_pessoa_fisica_w
from		atendimento_paciente c
where		c.nr_atendimento = NEW.nr_atendimento;

if (cd_pessoa_fisica_w is null) then
	select	max(c.nr_atendimento),
				max(c.cd_pessoa_fisica)
	into STRICT		nr_atendimento_w,
				cd_pessoa_fisica_w
	from		pepo_cirurgia c
	where		c.nr_sequencia = NEW.nr_seq_pepo;
end if;

if (NEW.dt_liberacao is null) then
	ie_tipo_w := 'ESP';
elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
	ie_tipo_w := 'XESP';
end if;

if (ie_tipo_w	is not null) then
	CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, cd_pessoa_fisica_w, nr_atendimento_w, NEW.nm_usuario);
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_escala_possum_after_insert() FROM PUBLIC;

CREATE TRIGGER escala_possum_after_insert
	AFTER INSERT OR UPDATE ON escala_possum FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_escala_possum_after_insert();

