SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS escala_ramsay_atual ON escala_ramsay CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_ramsay_atual() RETURNS trigger AS $BODY$
declare

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

  if (NEW.nr_hora is null) or (NEW.DT_AVALIACAO <> OLD.DT_AVALIACAO) then
    BEGIN  
    NEW.nr_hora	:= (to_char(round(NEW.DT_AVALIACAO,'hh24'),'hh24'))::numeric;
    end;
  end if;
  if (OBTER_SE_INTEGRACAO_ATIVA(962, 245) = 'S') then
    IF (OLD.DT_LIBERACAO is NULL) AND (NEW.DT_LIBERACAO is not NULL) THEN
      CALL integrar_sinais_vitais_bb(
          nr_sequencia_p => NEW.nr_sequencia,
          nr_atendimento_p => NEW.nr_atendimento,
          dt_sinal_vital_p => NEW.dt_avaliacao,
          qt_ramsay_p => NEW.ie_ramsay);
    END IF;

    IF (OLD.dt_inativacao is null AND NEW.dt_inativacao is not null) THEN
      CALL p_cancelar_flowsheet(NEW.nr_sequencia, NEW.nr_atendimento, 'C');
    END IF;
  end if;

end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_escala_ramsay_atual() FROM PUBLIC;

CREATE TRIGGER escala_ramsay_atual
	BEFORE INSERT OR UPDATE ON escala_ramsay FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_escala_ramsay_atual();

