SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS escala_richmond_atual ON escala_richmond CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_escala_richmond_atual() RETURNS trigger AS $BODY$
declare

BEGIN
  if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
    if (OBTER_SE_INTEGRACAO_ATIVA(977, 245) = 'S') then
      if (OLD.DT_LIBERACAO is NULL) and (NEW.DT_LIBERACAO is NOT NULL) then
        CALL integrar_sinais_vitais_bb(
            nr_sequencia_p => NEW.nr_sequencia,
            nr_atendimento_p => NEW.nr_atendimento,
            dt_sinal_vital_p => NEW.dt_avaliacao,
            ie_rass_p => NEW.ie_rass);
      end if;

      if (OLD.dt_inativacao is null AND NEW.dt_inativacao is not null) then
        CALL p_cancelar_flowsheet(NEW.nr_sequencia, NEW.nr_atendimento, 'F');
      end if;
    end if;

    if (NEW.nr_hora is null) or (NEW.DT_AVALIACAO <> OLD.DT_AVALIACAO) then
      BEGIN  
      NEW.nr_hora	:= (to_char(round(NEW.DT_AVALIACAO,'hh24'),'hh24'))::numeric;
      end;
    end if;
  end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_escala_richmond_atual() FROM PUBLIC;

CREATE TRIGGER escala_richmond_atual
	BEFORE INSERT OR UPDATE ON escala_richmond FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_escala_richmond_atual();

