declare
qt_reg_w	smallint;

BEGIN


if (NEW.nr_hora is null) or (NEW.DT_AVALIACAO <> OLD.DT_AVALIACAO) then
	BEGIN
	NEW.nr_hora	:= (to_char(round(NEW.DT_AVALIACAO,'hh24'),'hh24'))::numeric;
	end;
end if;
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

NEW.ie_risco	:=	null;

if (NEW.ie_tipo_cirurgia is not null) then
	if (NEW.ie_tipo_cirurgia < 15) then
		NEW.ie_risco	:=	 'A';
	elsif (NEW.ie_tipo_cirurgia < 40) then
		NEW.ie_risco	:=	 'I';
	else
		NEW.ie_risco	:=	 'B';
	end if;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
