CREATE OR REPLACE FUNCTION trigger_fct_escala_thompson_atual() RETURNS trigger AS $BODY$
declare
  qt_score_w smallint := 0;
  sql_w      varchar(400);
BEGIN
  BEGIN

  if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then
      BEGIN
          sql_w := 'call calcular_score_esc_thompson_md(:1, :2, :3, :4, :5, :6, :7, :8, :9) into :qt_score_w';

          EXECUTE sql_w using in NEW.ie_tonus,
                                        in NEW.ie_consciencia,
                                        in NEW.ie_convulsoes,
                                        in NEW.ie_postura,
                                        in NEW.ie_moro,
                                        in NEW.ie_preensao,
                                        in NEW.ie_succao,
                                        in NEW.ie_respiracao,
                                        in NEW.ie_fontanela,
                                        out qt_score_w;
      exception
          when others then
            qt_score_w := null;
      end;

      NEW.qt_score := qt_score_w;

  end if;	

  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
