DROP TRIGGER IF EXISTS esus_cad_individual_update ON esus_cad_individual CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_esus_cad_individual_update() RETURNS trigger AS $BODY$
declare

CD_AREA_W PSF_MICRO_AREA.cd_area%type;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	null;
end if;

if (NEW.NR_SEQ_MICRO_AREA <> OLD.NR_SEQ_MICRO_AREA)  then

    select cd_area
    into STRICT cd_area_w
    from PSF_MICRO_AREA
    where CD_MICRO_AREA = NEW.NR_SEQ_MICRO_AREA;

	update DOMICILIO_FAMILIA
    set NR_SEQ_MICROAREA = NEW.NR_SEQ_MICRO_AREA,
    NR_SEQ_AREA = cd_area_w
    where cd_pessoa_fisica = NEW.cd_pessoa_fisica;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
