DROP TRIGGER IF EXISTS evento_cont_param_estab_atual ON evento_contabil_param_estab CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_evento_cont_param_estab_atual() RETURNS trigger AS $BODY$
declare
ie_conta_vigente_w		varchar(1);
ie_tipo_w		conta_contabil.ie_tipo%type;

BEGIN

ie_conta_vigente_w	:= substr(obter_se_conta_vigente(NEW.cd_conta_contabil, LOCALTIMESTAMP),1,1);

if (NEW.cd_conta_contabil is not null) then
	BEGIN
	ie_tipo_w	:= substr(obter_dados_conta_contabil(NEW.cd_conta_contabil,null,'T'),1,1);
	if (ie_tipo_w = 'T') then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(43666, 'CTA=' || NEW.CD_CONTA_CONTABIL);
	end if;
	end;
end if;

if (ie_conta_vigente_w = 'N') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(232821,'CD_CONTA_CONTABIL_W=' || NEW.cd_conta_contabil);
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_evento_cont_param_estab_atual() FROM PUBLIC;

CREATE TRIGGER evento_cont_param_estab_atual
	BEFORE INSERT OR UPDATE ON evento_contabil_param_estab FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_evento_cont_param_estab_atual();

