DROP TRIGGER IF EXISTS evolucao_paciente_atual ON evolucao_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_evolucao_paciente_atual() RETURNS trigger AS $BODY$
DECLARE

cd_perfil_ativo_w	integer;
ie_relev_resumo_alta_w	varchar(10);
BEGIN
  BEGIN

cd_perfil_ativo_w	:=	Obter_perfil_Ativo;

IF (NEW.cd_perfil_ativo IS NULL) AND (cd_perfil_ativo_w <> 0) THEN		
	NEW.cd_perfil_ativo	:= cd_perfil_ativo_w;	
END IF;

if (NEW.DT_EVOLUCAO is not null) then
	NEW.ds_utc		:= substr(obter_data_utc(NEW.DT_EVOLUCAO, 'HV'),1,50);	
end if;

if	((OLD.DT_LIBERACAO is null) or (OLD.DT_LIBERACAO <> NEW.DT_LIBERACAO)) and (NEW.DT_LIBERACAO is not null) then
	NEW.ds_utc_atualizacao	:= substr(obter_data_utc(NEW.DT_LIBERACAO,'HV'),1,50);	
end if;

if ((OLD.DT_LIBERACAO_AUX is null) or (OLD.DT_LIBERACAO_AUX <> NEW.DT_LIBERACAO_AUX)) and (NEW.DT_LIBERACAO_AUX is not null) and (NEW.IE_AVALIADOR_AUX = 'S') and (NEW.CD_AVALIADOR_AUX is null) then
	NEW.CD_AVALIADOR_AUX :=  obter_pf_usuario(NEW.nm_usuario,'C');
end if;

BEGIN

if (NEW.cd_setor_atendimento is null) and (NEW.nr_atendimento is not null) then
	NEW.cd_setor_atendimento	:= obter_setor_atendimento(NEW.nr_atendimento);
end if;

exception
when others then
	null;
end;

  END;
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_evolucao_paciente_atual() FROM PUBLIC;

CREATE TRIGGER evolucao_paciente_atual
	BEFORE INSERT OR UPDATE ON evolucao_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_evolucao_paciente_atual();

