SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS evolucao_paciente_pend_atual ON evolucao_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_evolucao_paciente_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w		smallint;
ie_tipo_w		varchar(10);
nm_usuario_w		varchar(15) := NEW.nm_usuario;

qt_tempo_ca_w				wl_regra_item.qt_tempo_normal%type;
nr_seq_regra_wl_ca_w		wl_regra_item.nr_sequencia%type;
is_rule_tasklist_ca_w		wl_item.nr_sequencia%type;

c01 CURSOR FOR
SELECT	coalesce(b.qt_tempo_normal,0),
		coalesce(b.nr_sequencia, 0)
from 	wl_regra_worklist a,
		wl_regra_item b
where	a.nr_sequencia = b.nr_seq_regra
and		b.ie_situacao = 'A'
and		a.nr_seq_item = (	SELECT	max(x.nr_sequencia)
							from	wl_item x
							where	x.nr_sequencia = a.nr_seq_item
							and		x.cd_categoria = 'CA'
							and		x.ie_situacao = 'A');

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

if (NEW.dt_liberacao is null) then

     if (coalesce(NEW.IE_AVALIADOR_AUX,'N') = 'S') and (NEW.DT_LIBERACAO_AUX is not null)then
	
		select 	obter_usuario_pf(NEW.cd_medico)
		into STRICT	nm_usuario_w
		;		
		
		Update    pep_item_pendente
		set		nm_usuario = nm_usuario_w
		where	CD_EVOLUCAO = NEW.cd_evolucao;
		
	end if;

end if;

if (Obter_Valor_Param_Usuario(0,37, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento) = 'S') then
	if (coalesce(NEW.ie_avaliador_aux,'N') = 'S') and (NEW.dt_liberacao_aux is null) then
		ie_tipo_w := 'E';
		nm_usuario_w := NEW.nm_usuario_nrec;
	elsif (coalesce(NEW.ie_avaliador_aux,'N') = 'S') and (NEW.dt_liberacao_aux is not null) and (NEW.dt_liberacao is null) then
		ie_tipo_w := 'XE';		
		nm_usuario_w := NEW.nm_usuario_nrec;		
    CALL Gerar_registro_pendente_PEP(
      cd_tipo_registro_p => ie_tipo_w,
      nr_sequencia_registro_p => NEW.cd_evolucao,
      cd_pessoa_fisica_p => NEW.cd_pessoa_fisica,
      nr_atendimento_p => NEW.nr_atendimento, 
      nm_usuario_p => nm_usuario_w,
      nr_atend_cons_pepa_p=> NEW.nr_seq_atend_cons_pepa
    );		
		ie_tipo_w := 'E';
		nm_usuario_w := substr(obter_usuario_pessoa(NEW.cd_medico),1,15);
	elsif (NEW.dt_liberacao is null) then
		ie_tipo_w := 'E';
		nm_usuario_w := NEW.nm_usuario;				
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XE';		
		nm_usuario_w := NEW.nm_usuario;				
	end if;

	if (ie_tipo_w	is not null)then
    CALL Gerar_registro_pendente_PEP(
      cd_tipo_registro_p => ie_tipo_w,
      nr_sequencia_registro_p => NEW.cd_evolucao,
      cd_pessoa_fisica_p => NEW.cd_pessoa_fisica,
      nr_atendimento_p => NEW.nr_atendimento, 
      nm_usuario_p => nm_usuario_w,
      nr_atend_cons_pepa_p=> NEW.nr_seq_atend_cons_pepa
    );
	end if;
end if;

if (pkg_i18n.get_user_locale = 'ja_JP') then
BEGIN
	select	count(a.nr_sequencia)
	into STRICT	is_rule_tasklist_ca_w
	from	wl_item a,
			wl_regra_worklist b,
			wl_regra_item c
	where	a.nr_sequencia = b.nr_seq_item
	and	b.nr_sequencia = c.nr_seq_regra
	and	a.cd_categoria = 'CA'
	and	a.ie_situacao = 'A'
	and	c.ie_situacao = 'A';

if (OLD.dt_liberacao is null and NEW.dt_liberacao is not null and is_rule_tasklist_ca_w > 0 ) then

	open c01;
	loop
	fetch c01 into
		qt_tempo_ca_w,
		nr_seq_regra_wl_ca_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		BEGIN
			if (coalesce(qt_tempo_ca_w,0) > 0) then

				CALL wl_gerar_finalizar_tarefa('CA','I',NEW.nr_atendimento,NEW.cd_pessoa_fisica,wheb_usuario_pck.get_nm_usuario,LOCALTIMESTAMP+(qt_tempo_ca_w/24),'N',
										NEW.IE_TIPO_EVOLUCAO,null,null,null,null,null,null,null,null,nr_seq_regra_wl_ca_w,null,null,null,NEW.cd_evolucao,null,null,null,LOCALTIMESTAMP,null,null,null,null,null,null,null,null);
                    
			end if;
		end;
	end loop;
	close c01;
  end if;
end;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_evolucao_paciente_pend_atual() FROM PUBLIC;

CREATE TRIGGER evolucao_paciente_pend_atual
	AFTER INSERT OR UPDATE ON evolucao_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_evolucao_paciente_pend_atual();

