CREATE OR REPLACE FUNCTION trigger_fct_evolucao_paciente_sbis_ins() RETURNS trigger AS $BODY$
declare

nr_log_seq_w		bigint;
ie_inativacao_w		varchar(1);
	
BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then
IF (TG_OP = 'INSERT') THEN
	select 	nextval('log_alteracao_prontuario_seq')
	into STRICT 	nr_log_seq_w
	;
	
	insert into log_alteracao_prontuario(nr_sequencia, 
										 dt_atualizacao, 
										 ds_evento, 
										 ds_maquina, 
										 cd_pessoa_fisica, 
										 ds_item,
										 nm_usuario,
										 nr_atendimento,
										 nm_usuario_nrec) values (nr_log_seq_w, 
										 LOCALTIMESTAMP, 
										obter_desc_expressao(656665) ,  
										 wheb_usuario_pck.get_nm_maquina, 
									     coalesce(obter_pessoa_atendimento(NEW.nr_atendimento,'C'), NEW.cd_pessoa_fisica), 
									     obter_desc_expressao(792013),
										 coalesce(wheb_usuario_pck.get_nm_usuario,NEW.nm_usuario),
										 NEW.nr_atendimento,
										 NEW.nm_usuario_nrec);
else
	ie_inativacao_w := 'N';

	
	select 	nextval('log_alteracao_prontuario_seq')
	into STRICT 	nr_log_seq_w
	;
	
	 if (OLD.dt_inativacao is null) and (NEW.dt_inativacao is not null) then
      ie_inativacao_w := 'S';	
    end if;
	
	insert into log_alteracao_prontuario(nr_sequencia, 
										 dt_atualizacao, 
										 ds_evento, 
										 ds_maquina, 
										 cd_pessoa_fisica, 
										 ds_item,
										 nm_usuario,
										 nr_atendimento,
										 nm_usuario_nrec) values (nr_log_seq_w, 
										 LOCALTIMESTAMP, 
										 CASE WHEN ie_inativacao_w='N' THEN  obter_desc_expressao(302570)   ELSE obter_desc_expressao(331011) END , 
										 wheb_usuario_pck.get_nm_maquina, 
									     coalesce(obter_pessoa_atendimento(NEW.nr_atendimento,'C'), NEW.cd_pessoa_fisica), 
									    obter_desc_expressao(792013),
										 coalesce(wheb_usuario_pck.get_nm_usuario,NEW.nm_usuario),
										 NEW.nr_atendimento,
										 NEW.nm_usuario_nrec);
end if;
end if;
RETURN NEW;
end
$BODY$
