SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS exame_lab_result_item_covid ON exame_lab_result_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_exame_lab_result_item_covid() RETURNS trigger AS $BODY$
declare

nr_atendimento_w  atendimento_paciente.nr_atendimento%type;
nr_seq_atend_adic_w	ATEND_PACIENTE_ADIC.nr_sequencia%type;

BEGIN

--Raise_application_error(-20011,'#@#@'||PFCS_LAB_OBTER_SE_EXAME_COVID(:new.nr_seq_exame));
if (PFCS_LAB_OBTER_SE_EXAME_COVID(NEW.nr_seq_exame) = 'S') then

	select MAX(nr_atendimento)
	into STRICT   nr_atendimento_w
	from   exame_lab_resultado
	where  nr_seq_resultado = NEW.nr_seq_resultado;

	if (nr_atendimento_w is not null) then		

		select MAX(nr_sequencia)
		into STRICT   nr_seq_atend_adic_w
		from   ATEND_PACIENTE_ADIC
		where  nr_atendimento = nr_atendimento_w;		 	

		if ((trim(both substr(upper(NEW.ds_resultado),1,7))) like(substr(upper(OBTER_DESC_EXPRESSAO(296109)),1,7))) then 		

		--	Raise_application_error(-20011,'#@#@'||upper(:new.ds_resultado)||' - '||upper(OBTER_DESC_EXPRESSAO(296109))||' - '||nr_seq_atend_adic_w);
			if (nr_seq_atend_adic_w is null) then
				insert into ATEND_PACIENTE_ADIC(	nr_sequencia,
														dt_atualizacao,
														nm_usuario,
														nr_atendimento,
														ie_atend_covid_posit)
				values (nextval('atend_paciente_adic_seq'),LOCALTIMESTAMP,NEW.nm_usuario,nr_atendimento_w,'S');
			else
				update ATEND_PACIENTE_ADIC set ie_atend_covid_posit = 'S' where nr_sequencia = nr_seq_atend_adic_w;
			end if;
		else
		--	Raise_application_error(-20011,'#@#@'||upper(:new.ds_resultado)||' - '||upper(OBTER_DESC_EXPRESSAO(296109))||' - '||nr_seq_atend_adic_w);
			if (nr_seq_atend_adic_w is null) then



				insert into ATEND_PACIENTE_ADIC(	nr_sequencia,
														dt_atualizacao,
														nm_usuario,
														nr_atendimento,
														ie_atend_covid_posit)
				values (nextval('atend_paciente_adic_seq'),LOCALTIMESTAMP,NEW.nm_usuario,nr_atendimento_w,'N');
			else
				update ATEND_PACIENTE_ADIC set ie_atend_covid_posit = 'N' where nr_sequencia = nr_seq_atend_adic_w;
			end if;

		end if;

	end if;

end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_exame_lab_result_item_covid() FROM PUBLIC;

CREATE TRIGGER exame_lab_result_item_covid
	BEFORE UPDATE ON exame_lab_result_item FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_exame_lab_result_item_covid();

