DROP TRIGGER IF EXISTS extrato_cartao_cr_bef_insert ON extrato_cartao_cr CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_extrato_cartao_cr_bef_insert() RETURNS trigger AS $BODY$
declare

nr_sequencia_w		extrato_cartao_cr_arq.nr_sequencia%type;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

	if (NEW.ie_movto_banco <> coalesce(OLD.ie_movto_banco,'P')) then
	
		select	max(a.nr_sequencia)
		into STRICT	nr_sequencia_w
		from	extrato_cartao_cr_arq a
		where	a.nr_seq_extrato = 	NEW.nr_sequencia
		and	a.dt_baixa		is not null
		and	NEW.nr_seq_grupo	is not null;
		
		if (nr_sequencia_w is not null) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1201764);
		end if;
	
	end if;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
