declare

ds_call_stack_w	varchar(500);
ds_log_w	varchar(2000);


BEGIN

if (OLD.QT_DISPENSAR <> NEW.QT_DISPENSAR) and (OLD.QT_DISPENSAR is not null) then

	ds_call_stack_w	:= substr(dbms_utility.format_call_stack,1,499);
	ds_log_w := substr(':old.QT_DISPENSAR: ' || OLD.QT_DISPENSAR || ' :new.QT_DISPENSAR: ' || NEW.QT_DISPENSAR || ' :new.nm_usuario: ' || NEW.nm_usuario || ' :new.dt_atualizacao: ' || NEW.dt_atualizacao || ' :new.nr_sequencia:' || NEW.nr_sequencia || ' :new.nr_seq_fa_entrega: ' || NEW.nr_seq_fa_entrega || ' ds_call_stack_w:' || ds_call_stack_w,1,2000);

	insert into log_atendimento(dt_atualizacao,nm_usuario,cd_log,ds_log,nr_atendimento)
	values (LOCALTIMESTAMP,NEW.nm_usuario,61,ds_log_w,0);

end if;

RETURN NEW;
end
