DROP TRIGGER IF EXISTS fa_receita_farmacia_pend_atual ON fa_receita_farmacia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_fa_receita_farmacia_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w				smallint;
ie_tipo_w				varchar(10);
ie_liberacao_parcial_w	varchar(10);
ie_lib_receita_w		varchar(10);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

ie_liberacao_parcial_w := obter_param_usuario(10015, 88, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_liberacao_parcial_w);

if (coalesce(ie_liberacao_parcial_w,'N') <> 'P') then
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'RECAMB';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XRECAMB';
	end if;
else
	if (NEW.dt_liberacao_parcial is null) then
		ie_tipo_w := 'RECAMB';
	elsif (OLD.dt_liberacao_parcial is null) and (NEW.dt_liberacao_parcial is not null) then
		ie_tipo_w := 'XRECAMB';
	end if;
end if;

if (NEW.dt_cancelamento is not null) then
	ie_tipo_w := 'XRECAMB';
end if;

if (ie_tipo_w	is not null) then
  CALL Gerar_registro_pendente_PEP(
      cd_tipo_registro_p => ie_tipo_w,
      nr_sequencia_registro_p => NEW.nr_sequencia,
      cd_pessoa_fisica_p => coalesce(NEW.cd_pessoa_fisica,substr(obter_pessoa_atendimento(NEW.nr_atendimento,'C'),1,255)),
      nr_atendimento_p => NEW.nr_atendimento, 
      nm_usuario_p => NEW.nm_usuario,
      nr_atend_cons_pepa_p=> NEW.nr_seq_atend_cons_pepa
    );
end if;
	
<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
