DROP TRIGGER IF EXISTS feriado_atual ON feriado CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_feriado_atual() RETURNS trigger AS $BODY$
DECLARE
dt_atualizacao 	timestamp	:= LOCALTIMESTAMP;
BEGIN
  BEGIN
BEGIN

delete	from agenda_controle_horario
where	dt_agenda >= trunc(LOCALTIMESTAMP)
and	dt_agenda between trunc(NEW.dt_feriado) and trunc(NEW.dt_feriado) + 86399 / 86400;

NEW.dt_feriado := trunc(NEW.dt_feriado);

exception
	when others then
      	dt_atualizacao := LOCALTIMESTAMP;
end;
  END;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_feriado_atual() FROM PUBLIC;

CREATE TRIGGER feriado_atual
	BEFORE INSERT OR UPDATE ON feriado FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_feriado_atual();

