DROP TRIGGER IF EXISTS fluxo_caixa_agendamento_after ON fluxo_caixa_agendamento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_fluxo_caixa_agendamento_after() RETURNS trigger AS $BODY$
declare

qt_job_w	bigint := 0;
jobno		bigint;
ie_cria_job_w	varchar(1);

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

ie_cria_job_w := obter_param_usuario(830, 44, wheb_usuario_pck.get_cd_perfil, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_cria_job_w);

if (ie_cria_job_w = 'S') then
	select	count(*)
	into STRICT	qt_job_w
	from	user_jobs
	where	upper(what) like '%GERAR_DOCTO_FLUXO_CAIXA%'
	and 	broken = 'N';

	if (qt_job_w = 0) then
		dbms_job.submit(jobno, 'GERAR_DOCTO_FLUXO_CAIXA;', LOCALTIMESTAMP, 'SYSDATE + 1/24');
	end if;
end if;
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
