SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS funcao_parametro_dt_param ON funcao_parametro CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_funcao_parametro_dt_param() RETURNS trigger AS $BODY$
declare
	ie_consistir_valor_w	varchar(1);
	vl_parametro_padrao_w	varchar(255);
	ds_user_w 		varchar(100);
BEGIN

select	max(USERNAME)
into STRICT	ds_user_w
from	v$session
 where 	audsid = (SELECT userenv('sessionid') );

if (ds_user_w = 'TASY') then
	update	funcao
	set	dt_atualizacao_param = LOCALTIMESTAMP,
	dt_atualizacao = LOCALTIMESTAMP,
    	nm_usuario = NEW.nm_usuario	
	where	cd_funcao = NEW.cd_funcao;
end	if;

if (NEW.ie_consistir_valor = 'S') and (OLD.vl_parametro <> NEW.vl_parametro) and
	(somente_numero(NEW.vl_parametro) <  (somente_numero(NEW.vl_parametro_padrao))) then	
	CALL wheb_mensagem_pck.exibir_mensagem_abort(55208);
end	if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_funcao_parametro_dt_param() FROM PUBLIC;

CREATE TRIGGER funcao_parametro_dt_param
	AFTER UPDATE ON funcao_parametro FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_funcao_parametro_dt_param();

