DROP TRIGGER IF EXISTS funcao_param_perfil_dt_param ON funcao_param_perfil CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_funcao_param_perfil_dt_param() RETURNS trigger AS $BODY$
declare
	ie_consistir_valor_w	varchar(1);
	vl_parametro_padrao_w	varchar(255);
BEGIN

update	funcao
set	dt_atualizacao_param = LOCALTIMESTAMP
where	cd_funcao = NEW.cd_funcao;

if (TG_OP = 'INSERT' or TG_OP = 'UPDATE') then
	select	ie_consistir_valor,
		vl_parametro_padrao
	into STRICT	ie_consistir_valor_w,
		vl_parametro_padrao_w
	from	funcao_parametro
	where	cd_funcao = coalesce(NEW.cd_funcao, OLD.cd_funcao)
	and	nr_sequencia = coalesce(NEW.nr_sequencia, OLD.nr_sequencia);	

	if (ie_consistir_valor_w = 'S') and (somente_numero(NEW.vl_parametro) < somente_numero(vl_parametro_padrao_w)) then	
		CALL wheb_mensagem_pck.exibir_mensagem_abort(55208);
	end if;
end if;

IF TG_OP = 'DELETE' THEN
	RETURN OLD;
ELSE
	RETURN NEW;
END IF;

end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
