DROP TRIGGER IF EXISTS gca_atendimento_atual ON gca_atendimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_gca_atendimento_atual() RETURNS trigger AS $BODY$
declare
 
cd_setor_atendimento_w	integer;
BEGIN 
-- OS 209826 - Wilbert 
--if	(:new.cd_setor_paciente is null) then 
--	:new.cd_setor_paciente	:= obter_setor_Atendimento(:new.nr_atendimento); 
--end if; 
if (NEW.cd_setor_paciente is null) then 
	select	cd_setor_atendimento 
	into STRICT	cd_setor_atendimento_w 
	from	atend_paciente_unidade 
	where	nr_seq_interno = ( 
		SELECT Obter_Atepacu_paciente(NEW.nr_atendimento, 'IAA') 
		);
	NEW.cd_setor_paciente	:= cd_setor_atendimento_w;
end if;
 
 
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_gca_atendimento_atual() FROM PUBLIC;

CREATE TRIGGER gca_atendimento_atual
	BEFORE INSERT OR UPDATE ON gca_atendimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_gca_atendimento_atual();

