SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS gca_atendimento_update ON gca_atendimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_gca_atendimento_update() RETURNS trigger AS $BODY$
declare
qt_reg_w	smallint;
cd_setor_atendimento_w	bigint;
qt_idade_alerta_w		bigint;
BEGIN
  BEGIN 
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N') then 
	goto Final;
end if;
BEGIN	 
 
 
 
BEGIN 
cd_setor_atendimento_w	:= coalesce(obter_setor_atendimento(NEW.nr_atendimento),0);
exception 
	when others then 
	cd_setor_atendimento_w	:= 0;
	 
end;
 
qt_idade_alerta_w	:= coalesce(obter_idade_pf(obter_pessoa_atendimento(NEW.nr_atendimento,'C'),LOCALTIMESTAMP,'DIA'),0);
 
select	nr_sequencia 
into STRICT	NEW.nr_seq_gradacao 
from	gca_gradacao 
where	NEW.qt_pontuacao between vl_inicial and vl_final 
and		coalesce(cd_setor_atendimento,cd_setor_atendimento_w)	= cd_setor_atendimento_w 
and		ie_situacao = 'A' 
and		qt_idade_alerta_w	between coalesce(OBTER_IDADE_GCA_GRAD(nr_sequencia,'MIN'),0) 	and	coalesce(OBTER_IDADE_GCA_GRAD(nr_sequencia,'MAX'),9999999) 
and		(((ie_autor is null) or (NEW.ie_autor is null)) or (NEW.ie_autor = ie_autor));
exception 
	when others then 
		NEW.nr_seq_gradacao	:= null;
 
end;
 
<<Final>> 
qt_reg_w	:= 0;
 
  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_gca_atendimento_update() FROM PUBLIC;

CREATE TRIGGER gca_atendimento_update
	BEFORE UPDATE ON gca_atendimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_gca_atendimento_update();

