CREATE OR REPLACE FUNCTION trigger_fct_gca_atend_result_atual() RETURNS trigger AS $BODY$
BEGIN

if (NEW.nr_seq_result is not null) then
	select	qt_ponto
	into STRICT	NEW.qt_pontuacao
	from	gca_item_result
