SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS ged_atendimento_atual ON ged_atendimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ged_atendimento_atual() RETURNS trigger AS $BODY$
declare

qt_autorizacao_w	integer;
qt_anexo_w	integer;
nr_seq_anexo_agenda_w	anexo_agenda.nr_sequencia%type;

BEGIN

if (NEW.cd_pessoa_fisica is null) and (NEW.nr_atendimento is not null) then
	NEW.cd_pessoa_fisica := obter_pessoa_atendimento(NEW.nr_atendimento,'C');
end if;

if (coalesce(OLD.DT_REGISTRO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_REGISTRO) and (NEW.DT_REGISTRO is not null) then
	NEW.ds_utc		:= obter_data_utc(NEW.DT_REGISTRO, 'HV');	
end if;

if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
	NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
end if;

/* OS 2007482 - - Insercao de anexo agenda da Gestao da Agenda Cirurgica - Jeferson Job (jjrsantos) */


select	count(*)
into STRICT		qt_autorizacao_w
from	autorizacao_convenio
where	nr_atendimento = NEW.nr_atendimento
and		nr_seq_agenda = NEW.nr_seq_agenda;

if (qt_autorizacao_w > 0) then
	select	count(*)
	into STRICT		qt_anexo_w
	from	anexo_agenda
	where	ds_arquivo = NEW.ds_arquivo
	and 		nr_seq_agenda = NEW.nr_seq_agenda;
	
	if ( qt_anexo_w = 0 ) then
		select	nextval('anexo_agenda_seq')
		into STRICT	nr_seq_anexo_agenda_w
		;
			
		insert into anexo_agenda(
				nr_sequencia,
				ds_arquivo,
				nr_seq_agenda,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ds_observacao,
				ie_situacao
			)
		values (
			nr_seq_anexo_agenda_w,
			NEW.ds_arquivo,
			NEW.nr_seq_agenda,
			LOCALTIMESTAMP,
			NEW.nm_usuario,
			LOCALTIMESTAMP,
			NEW.nm_usuario_nrec,
			NEW.ds_observacao,
			'A'
			);
	end if;
end if;
/* FIM - Insercao de anexo agenda da Gestao da Agenda Cirurgica */


RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ged_atendimento_atual() FROM PUBLIC;

CREATE TRIGGER ged_atendimento_atual
	BEFORE INSERT OR UPDATE ON ged_atendimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ged_atendimento_atual();

