SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS gerencia_wheb_before_update ON gerencia_wheb CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_gerencia_wheb_before_update() RETURNS trigger AS $BODY$
declare

    qt_grupo bigint := 0;

BEGIN

    if (wheb_usuario_pck.get_ie_executar_trigger = 'N') then
		goto Final;
	end if;

    if (TG_OP = 'UPDATE') and (OLD.ie_tipo_grupo <> NEW.ie_tipo_grupo) then

        if (OLD.ie_tipo_grupo = 'S' and NEW.ie_tipo_grupo = 'D') then

            select count(1)
              into STRICT qt_grupo
              from grupo_suporte
             where nr_seq_gerencia_sup = NEW.nr_sequencia;

        elsif (OLD.ie_tipo_grupo = 'D' and NEW.ie_tipo_grupo = 'S') then

            select count(1)
              into STRICT qt_grupo
              from grupo_desenvolvimento
             where nr_seq_gerencia = NEW.nr_sequencia;

        end if;

        if (qt_grupo > 0) then

            CALL wheb_mensagem_pck.exibir_mensagem_abort(1162133);

        end if;

    end if;

    <<Final>>
    
    qt_grupo := 0;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_gerencia_wheb_before_update() FROM PUBLIC;

CREATE TRIGGER gerencia_wheb_before_update
	BEFORE UPDATE ON gerencia_wheb FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_gerencia_wheb_before_update();

