DROP TRIGGER IF EXISTS gerint_evento_int_aftinsert ON gerint_evento_integracao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_gerint_evento_int_aftinsert() RETURNS trigger AS $BODY$
declare

reg_integracao_w			gerar_int_padrao.reg_integracao; --Nao e utilizado. Apenas criado pois e obrigatorio na chamada da procedure GRAVAR_INTEGRACAO.
nr_seq_evento_w				integer;

BEGIN
	/*Somente o evento 12 e 15 sera gerado aqui, pois ele nao envia dados, apenas recebe.
	  Os demais eventos sao tratados na Trigger GERINT_EV_INT_DADOS_AFTINSERT*/

	if (NEW.id_evento = 12) then
		nr_seq_evento_w := 100;
	else
		nr_seq_evento_w := 124;
	end if;
	
	reg_integracao_w.cd_estab_documento := NEW.cd_Estabelecimento;
		
	if ((NEW.id_evento = 12) or (NEW.id_evento = 15)) and (NEW.ie_situacao = 'N') then
		reg_integracao_w := gerar_int_padrao.gravar_integracao(nr_seq_evento_w, NEW.nr_sequencia, NEW.nm_usuario, reg_integracao_w);
	end if;
	
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
