SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS gestao_vaga_delete ON gestao_vaga CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_gestao_vaga_delete() RETURNS trigger AS $BODY$
declare

nr_seq_interno_w	bigint;
qt_reg_w		smallint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	max(nr_seq_interno)
into STRICT	nr_seq_interno_w
from	unidade_atendimento
where	cd_unidade_basica	= OLD.cd_unidade_basica
and	cd_unidade_compl	= OLD.cd_unidade_compl
and	cd_setor_atendimento	= OLD.cd_setor_desejado;

if (nr_seq_interno_w is not null) then
	update	agenda_paciente
	set	nr_seq_interno 	 = NULL
	where	nr_sequencia 	= OLD.nr_seq_agenda
	and	nr_seq_interno 	= nr_seq_interno_w;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN OLD;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_gestao_vaga_delete() FROM PUBLIC;

CREATE TRIGGER gestao_vaga_delete
	AFTER DELETE ON gestao_vaga FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_gestao_vaga_delete();

