CREATE OR REPLACE FUNCTION trigger_fct_gpi_cron_etapa_atual() RETURNS trigger AS $BODY$
declare
nr_seq_cronograma_w	bigint;
nr_seq_superior_w		bigint;

pragma autonomous_transaction;

BEGIN
if (NEW.dt_inicio_prev is not null) and (NEW.dt_fim_prev is not null) and (NEW.dt_inicio_prev > NEW.dt_fim_prev) then
	--r.aise_application_error(-20011,'A data de início prevista deve ser menor que a data final prevista para a etapa.#@#@');
	CALL wheb_mensagem_pck.exibir_mensagem_abort(234610);
end if;

if (NEW.nr_seq_superior is not null) then
	BEGIN
	if (NEW.nr_seq_superior = NEW.nr_sequencia) then
		--r.aise_application_error(-20011,'Não é possível selecionar a mesma etapa como superior!#@#@');
		CALL wheb_mensagem_pck.exibir_mensagem_abort(234611);
	elsif (coalesce(OLD.nr_seq_superior,0) <> coalesce(NEW.nr_seq_superior,0)) then
		BEGIN
		nr_seq_superior_w	:= NEW.nr_seq_superior;

		while(nr_seq_superior_w is not null) loop
			BEGIN
			if (nr_seq_superior_w = NEW.nr_sequencia) then
				--r.aise_application_error(-20011,'Não é possível selecionar uma etapa inferior como superior!#@#@');
				CALL wheb_mensagem_pck.exibir_mensagem_abort(234612);
			end if;

			select	nr_seq_superior
			into STRICT	nr_seq_superior_w
			from	gpi_cron_etapa
			where	nr_sequencia = nr_seq_superior_w;
			end;
		end loop;
		end;
	end if;
	end;
end if;


RETURN NEW;
end
$BODY$
