	values (	nextval('gpi_log_estagio_seq'),
		LOCALTIMESTAMP,
		NEW.nm_usuario,
		LOCALTIMESTAMP,
		NEW.nr_sequencia,
		NEW.nr_seq_estagio
	);

end if;


RETURN NEW;
end
