SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS grupo_emp_estrutura_atual ON grupo_emp_estrutura CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_grupo_emp_estrutura_atual() RETURNS trigger AS $BODY$
declare
dt_inicio_vigencia_w	timestamp;
dt_fim_vigencia_w	timestamp;

BEGIN

select	dt_inicio_vigencia,
	dt_fim_vigencia
into STRICT	dt_inicio_vigencia_w,
	dt_fim_vigencia_w
from	grupo_empresa
where	nr_sequencia	= NEW.nr_seq_grupo;

if (dt_fim_vigencia_w is not null) and (NEW.dt_inicio_vigencia is not null) and (trunc(NEW.dt_inicio_vigencia) > trunc(dt_fim_vigencia_w)) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1083685);
	/*A vigência da empresa não está dentro da vigência do grupo de empresas*/


end if;

if ((NEW.pr_participacao > 100) or (NEW.pr_participacao <= 0)) then
  CALL wheb_mensagem_pck.exibir_mensagem_abort(1084390);
end if;

BEGIN
if (dt_fim_vigencia_w is not null) and (dt_fim_vigencia_w < trunc(LOCALTIMESTAMP,'dd')) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1083686);
	/*Este grupo de empresa não está vigente. Não é possível alterar ou incluir empresa em grupo já finalizado*/


end if;
end;


RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_grupo_emp_estrutura_atual() FROM PUBLIC;

CREATE TRIGGER grupo_emp_estrutura_atual
	BEFORE INSERT OR UPDATE ON grupo_emp_estrutura FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_grupo_emp_estrutura_atual();

