SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS hdm_atend_insert_update ON mprev_atendimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_hdm_atend_insert_update() RETURNS trigger AS $BODY$
declare

nr_seq_grupo_coletivo_w bigint;
nr_seq_col_turma_w	bigint;

BEGIN

if (NEW.ie_individual_coletivo = 'C') and (NEW.nr_seq_turma is not null) then
	
	select	max(nr_seq_grupo_coletivo),
		max(nr_sequencia)
	into STRICT	nr_seq_grupo_coletivo_w,
		nr_seq_col_turma_w
	from	mprev_grupo_col_turma
	where	nr_sequencia = NEW.nr_seq_turma;
			
	if (mprev_obter_dados_grupo_atend(nr_seq_grupo_coletivo_w,'ST') = 'I') and (nr_seq_grupo_coletivo_w is not null) then
		--Nao e permitido gerar atendimento para grupo inativo.

		CALL wheb_mensagem_pck.exibir_mensagem_abort(901094);
	elsif (mprev_obter_dados_grupo_turma(nr_seq_col_turma_w,'ST') = 'N') and (nr_seq_col_turma_w is not null) then
		--Nao e permitido gerar atendimento para turma fora de vigencia.

		CALL wheb_mensagem_pck.exibir_mensagem_abort(901095);
	end if;
	
end if;

if(NEW.dt_inicio > NEW.dt_termino AND NEW.dt_termino is not null) then
    CALL wheb_mensagem_pck.exibir_mensagem_abort(407178);
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_hdm_atend_insert_update() FROM PUBLIC;

CREATE TRIGGER hdm_atend_insert_update
	BEFORE INSERT OR UPDATE ON mprev_atendimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_hdm_atend_insert_update();

