	end if;
end if;

if ((OLD.dt_fim_dialise <> NEW.dt_fim_dialise) or (OLD.dt_fim_dialise is null and NEW.dt_fim_dialise is not null)) then
	if (NEW.dt_inicio_dialise > NEW.dt_fim_dialise)then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1118870);
	end if;
end if;

RETURN NEW;
end;
