SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS hl7_mensagem_insert ON hl7_mensagem CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_hl7_mensagem_insert() RETURNS trigger AS $BODY$
DECLARE

cd_versao_w		bigint;
nr_seq_proj_origem_w	bigint;
nr_sequencia_w		bigint;
qt_reg_w		bigint;

C01 CURSOR FOR
	SELECT	m.nr_sequencia,
		m.nm_mensagem,
		m.nr_seq_projeto,
		m.nr_seq_tipo
	from	wheb_hl7_mensagem m,
		wheb_hl7_projeto p
	where 	m.nr_seq_projeto 	= p.nr_sequencia
	and	p.cd_versao 		= cd_versao_w
	and	m.nr_seq_tipo  		= NEW.nr_seq_tipo
	order by nr_sequencia;

hl7_mensagem_w c01%rowtype;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;


select	max(cd_versao)
into STRICT	cd_versao_w
from	hl7_projeto
where	nr_sequencia = NEW.nr_seq_projeto;

if (cd_versao_w is not null) then

	open c01;
	loop
	fetch c01 into
		hl7_mensagem_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		BEGIN
		CALL wheb_hl7_pck.copiar_hl7_segmento_original(hl7_mensagem_w.nr_sequencia,NEW.nr_sequencia,null);
		end;
	end loop;
	close C01;
end if;

<<Final>>
qt_reg_w	:= 0;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_hl7_mensagem_insert() FROM PUBLIC;

CREATE TRIGGER hl7_mensagem_insert
	AFTER INSERT ON hl7_mensagem FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_hl7_mensagem_insert();

